const data = $('Koordinatlarƒ± ƒ∞≈üle').first().json;
const marketingText = $input.first().json.message.content;
const lat = data.coordinates.lat;
const lon = data.coordinates.lon;

// Harita linkleri
const mapUrls = {
  googleMaps: `https://www.google.com/maps/@${lat},${lon},500m/data=!3m1!1e3`,
  googleEarth: `https://earth.google.com/web/@${lat},${lon},500a,1000d,35y,0h,45t,0r`,
  yandexMaps: `https://yandex.com/maps/?ll=${lon},${lat}&z=17&l=sat`,
  bingMaps: `https://www.bing.com/maps?cp=${lat}~${lon}&lvl=17&style=a`,
  tkgm: 'https://parselsorgu.tkgm.gov.tr'
};

// KML dosyasƒ±
const kmlContent = `<?xml version="1.0" encoding="UTF-8"?>
<kml xmlns="http://www.opengis.net/kml/2.2">
  <Document>
    <name>Arazi ${data.referansNo}</name>
    <Placemark>
      <name>${data.parsel.il} - ${data.parsel.ilce}</name>
      <description>Ada: ${data.parsel.ada}, Parsel: ${data.parsel.parsel}</description>
      <LookAt>
        <longitude>${lon}</longitude>
        <latitude>${lat}</latitude>
        <altitude>0</altitude>
        <heading>0</heading>
        <tilt>45</tilt>
        <range>1000</range>
      </LookAt>
      <Point>
        <coordinates>${lon},${lat},0</coordinates>
      </Point>
    </Placemark>
  </Document>
</kml>`;

// Google Earth Studio proje
const earthStudioProject = {
  version: '1.0',
  project: {
    name: `Arazi_${data.referansNo}`,
    duration: 10,
    fps: 30,
    resolution: { width: 1920, height: 1080 }
  },
  camera: {
    keyframes: [
      { time: 0, position: { latitude: lat, longitude: lon, altitude: 2000 }, rotation: { heading: 0, tilt: 45, roll: 0 } },
      { time: 5, position: { latitude: lat, longitude: lon, altitude: 500 }, rotation: { heading: 180, tilt: 60, roll: 0 } },
      { time: 10, position: { latitude: lat, longitude: lon, altitude: 300 }, rotation: { heading: 360, tilt: 75, roll: 0 } }
    ]
  }
};

return [{
  json: {
    ...data,
    marketingText,
    mapUrls,
    kmlContent,
    earthStudioProject
  }
}];

